import { useState } from "react";
import { useDropzone } from "react-dropzone";

export default function FileConverter() {
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState("");
    const [conversionType, setConversionType] = useState("pdf-to-excel");

    const conversionDescriptions = {
        "pdf-to-excel": "üìÑ Converts a PDF file into an Excel spreadsheet, extracting tables and structured data.",
        "pdf-to-word": "üìÑ Converts a PDF file into an editable Word document.",
        "pdf-to-image": "üìÑ Converts each page of a PDF into a separate image.",
        "pdf-to-text": "üìÑ Extracts plain text from a PDF file.",
        "merge-pdfs": "üìë Combines multiple PDFs into one single document.",
        "split-pdf": "üìë Splits a PDF into multiple smaller PDF files.",
        "compress-pdf": "üìâ Reduces the file size of a PDF without losing quality.",
        "image-to-pdf": "üñºÔ∏è Converts image files (JPG, PNG) into a PDF document.",
        "image-to-text": "üñºÔ∏è Extracts text from an image using OCR technology.",
        "csv-to-excel": "üìä Converts a CSV file into an Excel spreadsheet.",
        "excel-to-csv": "üìä Converts an Excel spreadsheet into a CSV file.",
        "word-to-pdf": "üìù Converts a Word document into a PDF file.",
        "text-to-pdf": "üìù Converts plain text into a formatted PDF document.",
        "json-to-csv": "üíæ Converts JSON data into a structured CSV file.",
        "mp3-to-wav": "üéµ Converts an MP3 audio file into a WAV format.",
        "video-to-gif": "üé¨ Converts a video clip into an animated GIF file."
    };

    const onDrop = (acceptedFiles) => {
        setFile(acceptedFiles[0]);
    };

    const { getRootProps, getInputProps } = useDropzone({
        onDrop,
        multiple: false,
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!file) {
            setMessage("‚ö†Ô∏è Please select a file.");
            return;
        }

        setMessage("‚è≥ Uploading and converting... Please wait.");
        const formData = new FormData();
        formData.append("file", file);
        formData.append("conversionType", conversionType);

        try {
            const response = await fetch("https://file-converter-backend-jl7o.onrender.com/upload", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                throw new Error("‚ùå File conversion failed.");
            }

            const blob = await response.blob();
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = `converted.${conversionType.split('-').pop()}`;
            document.body.appendChild(a);
            a.click();
            a.remove();
            setMessage("‚úÖ Conversion successful! File downloaded.");
        } catch (error) {
            setMessage("‚ùå Error converting file.");
        }
    };

    return (
        <div className="p-6 max-w-md mx-auto bg-green-100 rounded-xl shadow-md space-y-4 text-center">
            <h1 className="text-2xl font-bold text-green-800">üîÑ File Converter üåç</h1>
            <p className="text-green-700">üåü Select a conversion type and upload your file. üöÄ</p>

            <select 
                className="w-full p-2 border rounded bg-white text-green-900" 
                value={conversionType} 
                onChange={(e) => setConversionType(e.target.value)}
            >
                {Object.keys(conversionDescriptions).map((key) => (
                    <option key={key} value={key}>{conversionDescriptions[key].split(" ")[0]} {key.replace(/-/g, " ").toUpperCase()}</option>
                ))}
            </select>

            <div {...getRootProps()} className="border-2 border-green-500 border-dashed p-6 cursor-pointer bg-green-200">
                <input {...getInputProps()} />
                {file ? <p className="text-green-900">üìé {file.name}</p> : <p className="text-green-900">üì• Drop a file here, or click to select one.</p>}
            </div>

            <button onClick={handleSubmit} className="w-full bg-green-600 text-white p-2 rounded mt-3" disabled={!file}>
                üîÑ Convert
            </button>

            {message && <p className="text-sm text-green-800">{message}</p>}

            <p className="text-sm text-green-800 mt-3">‚ÑπÔ∏è {conversionDescriptions[conversionType]}</p>
        </div>
    );
}
